import React, { useEffect, useState } from "react";

import './homeLanding.css';

import ReservationRequest from "../reservationRequest/reservationRequest";

import lakeSunsetImage from '../../assets/testImages/lake-sunset.jpg';
import northShoreImage from '../../assets/testImages/north-shore.jpg';
import newFireworksImage from '../../assets/testImages/fireworks.jpg';
import newSkiingImage from '../../assets/testImages/skiing.jpg';
import reflectionImage from '../../assets/testImages/reflection.jpg';

const homeImages = [lakeSunsetImage, northShoreImage, newFireworksImage, newSkiingImage, reflectionImage];

function HomeLanding() {
    const [imageIndex, setImageIndex] = useState(0);

    const startImageFade = () => {
        const images = Array.from(document.querySelectorAll('.homeImage'));
        
        setNextActiveImage(images);
    };

    const setNextActiveImage = (images) => {
        const activeImageIndex = images.indexOf(document.querySelector('.homeImage.active'));
        const nextActiveImageIndex = activeImageIndex === homeImages.length - 1 
            ? 0 
            : activeImageIndex + 1;
        
        images[activeImageIndex].className = '';
        images[activeImageIndex].classList.add('homeImage', 'fadeOut');
        images[nextActiveImageIndex].classList.remove('fadeOut');
        images[nextActiveImageIndex].classList.add('active', 'fadeIn');

        setImageIndex(nextActiveImageIndex);
    };
    
    useEffect(() => {
        setTimeout(() => {
            startImageFade();
        }, 6000);
    }, [imageIndex]);

    return(
        <div id='landing'>
            <ReservationRequest />
            {
                homeImages.map((img, index) => {
                    return (
                        index === 0 
                            ? <div style={{ backgroundImage:`url(${img})`}} className='homeImage active' />
                            : <div style={{ backgroundImage:`url(${img})`}} className='homeImage' />
                    )
                })
            }
        </div>
    )
}

export default HomeLanding;
