import React, { useEffect, useState } from 'react';

import './home.css';

import forestImage from '../../assets/images/forest.png';
import skiliftImage from '../../assets/images/skilift.jpg';
import fireworksImage from '../../assets/images/fireworks.jpg';
import skiingImage from '../../assets/images/skiing.png';
import lakeFunImage from '../../assets/images/lake_fun.png';

const homeImages = [
    {
        url: forestImage,
        alt: 'forest'
    },
    {
        url: skiliftImage,
        alt: 'skilift'
    },
    {
        url: fireworksImage,
        alt: 'fireworks'
    },
    {
        url: skiingImage,
        alt: 'skiing'
    },
    {
        url: lakeFunImage,
        alt: 'lake fun'
    }
];


function HomePage() {
    const [currentImage, setCurrentImage] = useState({});
    const [imageIndex, setImageIndex] = useState(0);
    
    useEffect(() => {
        setCurrentImage(homeImages[imageIndex]);

        setTimeout(() => {
            if (imageIndex === homeImages.length -1) {
                setImageIndex(0);
            } else {
                setImageIndex(imageIndex + 1);
            }
        }, 5000);
    }, [imageIndex])

    return (
        <div id='landing'>
            <img id='homeBackgroundImage' src={currentImage.url} alt={currentImage.alt} />
            Hello World
        </div>
    )
}

export default HomePage;
