import axios from 'axios';

const EmailService = {
    sendReservation: async function(params) {
        const response = await axios.post(
            process.env.REACT_APP_STARDUST_EMAIL_URL,
            {
                service_id: 'service_snlbumc',
                template_id: 'stardust_reservation_request',
                user_id: 'FxjjHVQXAKFna-GrB',
                template_params: params
            }
        );

        return response.status === 200 ? true : false;
    },
    sendUserRegistration: async function(params) {
        const response = await axios.post(
            process.env.REACT_APP_STARDUST_EMAIL_URL,
            {
                service_id: 'service_snlbumc',
                template_id: 'stardust_user_registration',
                user_id: 'FxjjHVQXAKFna-GrB',
                template_params: params
            }
        );

        return response.status === 200 ? true : false;
    }
};

export default EmailService;
