import axios from 'axios';

let alerts;

const AlertsService = {
    getAlerts: async function() {
        const alertsUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/alerts`;
        await axios.get(alertsUrl).then((response) => {
            alerts = response.data;
        });

        return alerts;
    }
};

export default AlertsService;
