import React, { useEffect, useState } from 'react';
import { Document, Page } from 'react-pdf';
import PageFlip from 'react-pageflip';

import PageTitle from '../../components/pageTItle/pageTitle';

import HTMLService from '../../services/htmlService';
import NewslettersService from '../../services/newsletterService';

import './newsletters.css';

function NewslettersPage() {
    const [newsletter, setNewsletter] = useState({});
    const [newsletterList, setNewsletterList] = useState([]);

    useEffect(() => {
        async function getNewsletters() {
            let response = await NewslettersService.getNewsletters();

            if (response.length) {
                setNewsletter(response[0]);

                if (response.length > 1) {
                    response.shift();
                    setNewsletterList(response);
                }
            }
        }

        getNewsletters();
    }, [])

    useEffect(() => {
        console.log(newsletter);
        console.log(newsletterList);
    }, [newsletter, newsletterList])

    const PDFViewer = () => {
        const pdfUrl = HTMLService.sanatizeHTML(newsletter.content.rendered);
        console.log(pdfUrl);

        function onDocumentLoadSuccess(response) {
            console.log(response);
        }

        return (
            <Document file={pdfUrl} onLoadSuccess={onDocumentLoadSuccess()}>
                {/* {Array.from({ length: numPages }, (_, index) => (
                    <Page key={`page_${index + 1}`} pageNumber={index + 1} />
                ))} */}
            </Document>
        )
    }

    const FlipBook = ({ children }) => {
        return (
            <PageFlip>
                {children}
            </PageFlip>
        );
    };

    return (
        <div id='newsletters'>
            <PageTitle
                image="#"
                pageTitle="Newsletters"
            />
            <FlipBook>
                <PDFViewer />
            </FlipBook>
        </div>
    )
}

export default NewslettersPage;
