import React, { useRef, useState } from 'react';

import './registerForm.css';

function RegisterForm(props) {
    const emailRef = useRef('');
    const firstNameRef = useRef('');
    const lastNameRef = useRef('');
    const ownersRef = useRef('');
    const usernameRef = useRef('');

    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const changePassword = (e) => {
        setPassword(e.target.value);
    }

    const changeConfirmPassword = (e) => {
        setConfirmPassword(e.target.value)
    }

    const handleFormSubmit = () => {
        console.log('Register');
    }

    return (
        <form id='registerForm' onSubmit={handleFormSubmit}>
            <input type='text' name='username'  ref={usernameRef} />
            <input type='text' name='firstName'  ref={firstNameRef} />
            <input type='text' name='lastName'  ref={lastNameRef} />
            <input type='email' name='email' ref={emailRef} />
            <input type='text' name='owners'  ref={ownersRef} />
            <input type='password' name='password' onChange={changePassword} />
            <input type='password' name='confirmPassword' onChange={changeConfirmPassword} />
            <div className='button-row'>
                <button type='submit'>Register</button>
                <button onClick={props.toggleLogin}>Login</button>
            </div>
        </form>
    )
}

export default RegisterForm;
