import axios from 'axios';
import { useHistory } from 'react-router-dom';

const LoginService = {

    login: async function(email, passwrord) {
        const history = useHistory();
        history.push('/testsite/login', { data: 'my-data' });
        const response = await axios.post(
            'http://localhost/testsite/wp-json/jwt-auth/v1/token',
            {
                username: email,
                password: passwrord
            }
        );

        console.log(response);

        const token = response.data.token;

        if (!token) {
            alert('There was an issue logging in. If this error persists, please contact customer service.');
            return;
        }

        localStorage.clear();
        localStorage.setItem('user-token', token);
        
        history.push('/testsite');
    }
};

export default LoginService;
