import React, { useState, useEffect } from "react";
import { Routes, Route, Outlet } from 'react-router-dom';
import PrivateRoute from "./components/privateRoute/privateRoute";

import StardustNavbar from './components/navbar/navbar';
import Footer from './components/footer/footer';
import HomePage from './pages/home/home';
import ActivitiesPage from './pages/activities/activities';
import ReservationPage from './pages/reservations/reservations';
import LoginPage from './pages/login/login';
import RoomsPage from './pages/rooms/rooms';
import RoomPage from './pages/rooms/room/room';
import BoardOfDirectorsPage from './pages/boardOfDirectors/boardOfDirectors';
import NewslettersPage from './pages/newsletters/newsletters';

import "./App.css";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        if (localStorage.getItem('user-token')) {
            setIsAuthenticated(true);
        }
    }, []);

    return (
        <div className="App">
            <StardustNavbar 
                isAuthenticated={isAuthenticated}
                authChanger={setIsAuthenticated}
            />
            <Routes>
                <Route path='stardust' element={
                        <HomePage />
                    }
                />
                <Route path='stardust/activities' element={
                        <ActivitiesPage />
                    }
                />
                <Route path='stardust/rooms'>
                    <Route index={true} element={
                            <>
                                <RoomsPage />
                                <Outlet />
                            </>
                        }
                    />
                    <Route path=':roomId' element={
                            <>
                                <RoomPage />
                                <Outlet />
                            </>
                        }
                    />
                </Route>
                <Route path='stardust/reservations/:roomName' element={
                        <ReservationPage />
                    }
                />
                <Route path='stardust/directors' element={
                        <PrivateRoute>
                            <BoardOfDirectorsPage />
                        </PrivateRoute>
                    }
                />
                <Route path='stardust/newsletters' element={
                        <PrivateRoute>
                            <NewslettersPage />
                        </PrivateRoute>
                    }
                />
                <Route path='stardust/login' element={
                        <LoginPage authChanger={setIsAuthenticated} />
                    }
                />
                <Route path='*' element={
                        <h1>No Match</h1>
                    }
                />
            </Routes>
            <Footer />
        </div>
    );
}

export default App;
