import axios from 'axios';

let newsletters;
let boardMinutes;
let audits;
let meetingPDF;

let rules;
let calendar;

const PDFService = {
    getOwnersPDFs: async function() {
        const newslettersUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/newsletters?per_page=50`;
        await axios.get(newslettersUrl).then((response) => {
            newsletters = response.data;
        });

        const boardMinutesUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/board_minutes?per_page=50`;
        await axios.get(boardMinutesUrl).then((response) => {
            boardMinutes = response.data;
        });

        const auditsUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/audits?per_page=50`;
        await axios.get(auditsUrl).then((response) => {
            audits = response.data;
        });

        const meetingPDFUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/meeting_pdfs`;
        await axios.get(meetingPDFUrl).then((response) => {
            meetingPDF = response.data;
        });

        return {
            newsletters,
            boardMinutes,
            audits,
            meetingPDF
        };
    },

    getReservationPDFs: async function() {
        const rulesUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/rules`;
        await axios.get(rulesUrl).then((response) => {
            rules = response.data;
        });

        const calendarUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/usage_calendars`;
        await axios.get(calendarUrl).then((response) => {
            calendar = response.data;
        });

        return {
            rules,
            calendar
        }
    }
};

export default PDFService;
