import React, { useEffect, useState } from "react";

import './activityList.css';

const testActivities = [
    {
        image: '',
        title: 'History of Lake Tahoe',
        description: 'Come learn the history of Lake Tahoe'
    },
    {
        image: '',
        title: 'Water Aerobics',
        description: 'Enjoy an exhilarating workout with the Activities Staff inthe 86 degree pool. Lots of fun with great music!'
    },
    {
        image: '',
        title: 'Manager\'s Cocktail Hour',
        description: 'Free beer, wine, snacks, and raffles!'
    },
    {
        image: '',
        title: 'Bingo Night',
        description: 'Just like you remember and tons of fun! $2 per card or 3 cards for $5.'
    },
    {
        image: '',
        title: 'Beaded Jewelry Making',
        description: 'Join the fun of creating your own necklace or bracelet with a variety of beads. $5 per person.'
    },
    {
        image: '',
        title: 'Lasagna Dinner',
        description: 'Yummy homemade lasagne, salad, garlic bread, dessert, and drink! $7 per person.'
    },
    {
        image: '',
        title: 'Making Picture Frames',
        description: 'Make your own "Lake Tahoe Picture Frame". Great for kids of all ages. $2 per person.'
    },
    {
        image: '',
        title: 'Make Your Own Tie Dye T-Shirt',
        description: 'Learn how to make and create a t-shirt using colored dyes. $5 per shirt.'
    },
    {
        image: '',
        title: 'Blackjack Tournament',
        description: 'Play Black jack with an activities staff and you can win a Prize! (No money involved)'
    },
    {
        image: '',
        title: 'Karaoke Night!',
        description: 'Bring your singing voice over to activites and sing some songs! Fun for all ages. Chips and juice provided.'
    },
    {
        image: '',
        title: 'Chili Dinner',
        description: 'Plates include: Homemade chili, salad, corn bread, dessert, and drink. $7 per person'
    },
    {
        image: '',
        title: 'Power Walk with Activities Staff',
        description: 'Follow one of our Activities Staff in an invigorating walk to Ski Run or Lakeside Beach. A great way to start your morning.'
    },
    {
        image: '',
        title: 'Pine Cone Decorating',
        description: 'Take a local pine code souvenir home with you. Free.'
    },
    {
        image: '',
        title: 'Hot Cocoa & Roast Marshmallows',
        description: 'Come join the activity staff in making some smores and drinking hot cocoa around the fire pit. Lots of fun!'
    },
    {
        image: '',
        title: 'Nintendo Wii Fun!!',
        description: 'Join us for a Wii Dance Off on the big screen in the activites room. Fun for the whole family!'
    },
    {
        image: '',
        title: 'Kahle Meadows Walk',
        description: 'Follow one of our staff memebers to Kahle Meadows for a scenic walk.'
    },
    {
        image: '',
        title: 'Movie Night',
        description: 'Come join us in the Activites Room with the big-screen TV, fresh made buttery popcorn, and a drink.'
    }

]

function ActivityList() {
    const [activities, setActivities] = useState([]);

    useEffect(() => {
        fetch('http://localhost/testsite/wp-json/wp/v2/activitys')
            .then(response => response.json())
            .then(data => console.log(data));
    })

    return (
        <>
            {
                testActivities.map(activity => {
                    return <div className="activity">
                        <img src={activity.image} />
                        <p>{activity.title}</p>
                        <p>{activity.description}</p>
                    </div>
                })
            }
        </>
    )
}

export default ActivityList;
