import React, { useEffect, useState } from 'react';

import Alert from "react-bootstrap/Alert";

import AlertsService from '../../services/alertService';
import HTMLService from '../../services/htmlService';

const alertStyles = [
    'primary',
    'secondary',
    'success',
    'danger',
    'warning',
    'info',
    'light',
    'dark'
]

function SiteAlert() {
    const [alerts, setAlerts] = useState([]);
    const [showAlert, setShowAlert] = useState(false);

    useEffect(() => {
        async function getAlerts() {
            const alertList = await AlertsService.getAlerts();

            if (alertList.length) {
                setShowAlert(true);
            }

            setAlerts(alertList);
        }

        getAlerts();
    }, []);

    return (
        <>
            {
                alerts ? 
                    alerts.map(alert => {
                        const className = HTMLService.sanatizeHTML(alert.excerpt.rendered);
                        console.log(typeof(className))
                        console.log(alertStyles.includes(className))
                        console.log(alertStyles)
                        console.log(alertStyles.indexOf(className))
                        const alertClass = alertStyles.includes(className) ? className : 'danger';

                        return (
                            <Alert
                                key={alert.id}
                                variant={alertClass}
                                show={showAlert}
                                onClose={() => setShowAlert(false)}
                                className="mb-0"
                                dismissible
                            >
                                <p><strong>{alert.title.rendered}:</strong> {HTMLService.sanatizeHTML(alert.content.rendered)}</p>
                            </Alert>
                        )
                    })
                : <></>
            }
        </>
    )
}

export default SiteAlert;
