import React, { useEffect, useState, useRef } from "react";
import { useParams } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import DatePicker from 'react-datepicker';
import Row from 'react-bootstrap/Row';

import EmailService from "../../services/emailService";
import RoomsService from "../../services/roomsService";

import PageTitle from '../../components/pageTItle/pageTitle';

import reservationImage from "../../assets/images/reservation.jpg";

import './reservations.css';
import 'react-datepicker/dist/react-datepicker.css';

function ReservationPage() {
    const [endDate, setEndDate] = useState(new Date());
    const [phone, setPhone] = useState('');
    const [room, setRoom] = useState('default');
    const [roomTitles, setRoomTitles] = useState([{ label: 'Select an option...', value: 'default' }])
    const [startDate, setStartDate] = useState(new Date());
    const [use, setUse] = useState('default');

    const nameRef = useRef('');
    const emailRef = useRef('');
    const accountRef = useRef('');

    const { roomName } = useParams();

    const options = [
        { label: 'Select an option...', value: 'default' },
        { label: 'At Stardust 7-Night Week', value: 'At Stardust 7-Night Week' },
        { label: 'At Stardust Split Week', value: 'At Stardust Split Week' },
        { label: 'RCI Exchange', value: 'RCI Exchange' },
        { label: 'Interval International Exchange', value: 'Interval International Exchange' },
        { label: 'Rent Out', value: 'Rent Out' }
    ]

    useEffect(() => {
        async function fetchRoomTitles() {
            const roomTitleList = await RoomsService.getRoomTitles();
            const mergedArray = [...roomTitles, ...roomTitleList]
            setRoomTitles(mergedArray);
        }

        fetchRoomTitles();
        setRoom(roomName ? roomName : 'default');
    }, []);

    const handleFormSubmit = (e) => {
        e.preventDefault();

        const isValid = validateForm();

        if (isValid) {
            const params = {
                'name': nameRef.current.value,
                'phone': phone,
                'email': emailRef.current.value,
                'account': accountRef.current.value,
                'checkIn': startDate,
                'checkOut': endDate,
                'use': use
            };
            
            const success = EmailService.sendEmail(params);

            if (success) {
                resetForm();
            }
        }

    }

    const resetForm = () => {
        console.log('Form Reset');
    }

    const validateForm = () => {
        let validForm = true;

        return validForm;
    }

    const formatPhoneNumber = (input) => {
        const cleanedInput = input.replace(/\D/g, ''); // Remove non-digit characters
        const formattedInput = `(${cleanedInput.slice(0, 3)}) ${cleanedInput.slice(3, 6)}-${cleanedInput.slice(6)}`;
        return formattedInput;
    };

    const phoneChange = (e) => {
        setPhone(formatPhoneNumber(e.target.value));
    }

    const useChange = (e) => {
        setUse(e.target.value);
    }

    const roomChange = (e) => {
        setRoom(e.target.value);
    }

    return (
        <div id='reservation-request'>
            <PageTitle
                image={reservationImage}
                pageTitle="Request a Reservation"
            />
            <Container>
                <Form 
                    onSubmit={handleFormSubmit}
                    className="reservationForm"
                >
                    <Row>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>Name</Form.Label>
                                <Form.Control 
                                    type="text" 
                                    placeholder="John Smith" 
                                    ref={nameRef} 
                                    required 
                                />
                            </Form.Group>
                        </Col>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>E-Mail</Form.Label>
                                <Form.Control 
                                    type="email" 
                                    placeholder="John.Smith@example.com" 
                                    ref={emailRef} 
                                    required 
                                />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>Phone Number</Form.Label>
                                <Form.Control 
                                    type="text" 
                                    placeholder="(XXX) XXX-XXXX" 
                                    value={phone} 
                                    onChange={phoneChange} 
                                    required 
                                />
                            </Form.Group>
                        </Col>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>Account Number</Form.Label>
                                <Form.Control 
                                    type="text" 
                                    placeholder="Account Number" 
                                    ref={accountRef} 
                                />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>Start Date</Form.Label>
                                <DatePicker showIcon selected={startDate} onChange={(date) => setStartDate(date)} required />
                            </Form.Group>
                        </Col>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>End Date</Form.Label>
                                <DatePicker showIcon selected={endDate} onChange={(date) => setEndDate(date)} required />
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>How do you plan to use this room?</Form.Label>
                                <Form.Select 
                                    value={use} 
                                    onChange={useChange}
                                >
                                    {
                                        options.map((option, index) => {
                                            return <option value={option.value} key={index}>{option.label}</option>
                                        })
                                    }
                                </Form.Select>
                            </Form.Group>
                        </Col>
                        <Col xs={12} md={6}>
                            <Form.Group>
                                <Form.Label>Room Name</Form.Label>
                                <Form.Select 
                                    value={room} 
                                    onChange={roomChange}
                                >
                                    {
                                        roomTitles.map((option, index) => {
                                            return <option value={option.value} key={index}>{option.label}</option>
                                        })
                                    }
                                </Form.Select>
                            </Form.Group>
                        </Col>
                    </Row>
                    <Row>
                        <Button 
                            type="submit"
                        >
                            Submit Request
                        </Button>
                    </Row>
                </Form>
            </Container>
        </div>
    )
}

export default ReservationPage;
