import React, { useEffect, useState } from 'react';

import forestImage from '../../assets/images/forest.png';
import skiliftImage from '../../assets/images/skilift.jpg';
import lakeTahoeImage from '../../assets/images/lake_tahoe.png';
import fireworksImage from '../../assets/images/fireworks.png';
import skiingImage from '../../assets/images/skiing.png';
import lakeFunImage from '../../assets/images/lake_fun.png';

const homeImages = [
    {
        src: forestImage,
        alt: 'forest'
    },
    {
        src: skiliftImage,
        alt: 'skilift'
    },
    {
        src: lakeTahoeImage,
        alt: 'lake tahoe'
    },
    {
        src: fireworksImage,
        alt: 'fireworks'
    },
    {
        src: skiingImage,
        alt: 'skiing'
    },
    {
        src: lakeFunImage,
        alt: 'lake fun'
    }
]


function HomePage() {
    const [currentImage, setCurrentImage] = useState({});
    const [imageIndex, setImageIndex] = useState(0);
    
    useEffect(() => {
        setCurrentImage(homeImages[imageIndex]);
    })

    return (
        <img src={currentImage.src} alt={currentImage.alt} />
    )
}

export default HomePage;
