import axios from 'axios';

const LoginService = {
    login: async function(email, password) {
        try {
            const response = await axios.post(
                process.env.REACT_APP_STARDUST_BASE_URL + process.env.REACT_APP_STARDUST_LOGIN_URL,
                {
                    username: email,
                    password: password
                }
            );

            authToken = response.data.token;
            
            localStorage.clear();
            localStorage.setItem( 'auth-info', JSON.stringify({
                'userToken': authToken,
                'authDate': new Date()
            }));
            
            return authToken;
        }
        catch (error) {
            return '';
        }
    }
};

export default LoginService;
