import axios from 'axios';

let newsletters;
let boardMinutes;
let audits;
let meetingPDF;

const PDFService = {
    getPDFs: async function() {
        const newslettersUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/newsletters`;
        await axios.get(newslettersUrl).then((response) => {
            newsletters = response.data;
        });

        const boardMinutesUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/board_minutes`;
        await axios.get(boardMinutesUrl).then((response) => {
            boardMinutes = response.data;
        });

        const auditsUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/audits`;
        await axios.get(auditsUrl).then((response) => {
            audits = response.data;
        });

        const meetingPDFUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/meeting_pdfs`;
        await axios.get(meetingPDFUrl).then((response) => {
            meetingPDF = response.data;
        });

        return {
            newsletters,
            boardMinutes,
            audits
        };
    }
};

export default PDFService;
