import React from 'react';

import './roomList.css';

import LeftRoom from '../leftRoom/leftRoom';
import RightRoom from '../rightRoom/rightRoom';

import comet from '../../assets/images/room/comet.jpg';
import deluxe from '../../assets/images/room/deluxe.jpg';
import sun from '../../assets/images/room/sun.jpg';
import vipDeluxe from '../../assets/images/room/vip-deluxe.jpg';

const rooms = [
    {
        title: 'Stardust & VIP Deluxe Two Bedroom Suites',
        image: vipDeluxe,
        alt: 'vip-deluxe',
        description: 'Two-bedroom suite with queen beds in each bedroom and separate living area with queen sofa bed. Kitchen table and chairs, full refrigerator, microwave, glass and tableware stocked kitchenette, coffeemaker, toaster, blender, TV and DVD player.'
    },
    {
        title: 'Satellite & Sun Studio Suites',
        image: sun,
        alt: 'sun',
        description: 'Our studio units are packed with at-home living conveniences including kitchen and seating areas stocked with refrigerator, microwave, toaster, blender, on-request electric fry pan and hot plate, and all tableware.'
    },
    {
        title: 'Astro & Comet One Bedroom Suites',
        image: comet,
        alt: 'comet',
        description: 'One bedroom suite with one Queen bed, separate living area with queen sofa bed and armchair, kitchen table with chairs, refrigerator, microwave, kitchenette stocked with glass tableware and cookware, sink, coffeemaker, toaster, blender, TV with remote, DVD/CD player.'
    },
    {
        title: 'Starlite Deluxe Studio Suite',
        image: deluxe,
        alt: 'deluxe',
        description: 'Deluxe suite with modern décor and upgrade finishes, King bed, Full pull out sofa, fireplace, kitchen with table and chairs, refrigerator, microwave, kitchenette stocked with glass and tableware and more.'
    }
]

function RoomList() {
    return (
        <div id='room-list'>
            <div id='room-list-content'>
                <div id='room-header'>
                    <h1>Stardust Owners Suites</h1>
                    <hr />
                    <hr style={{ marginLeft: '-2.5%', width: '105%' }} />
                </div>
                <div id='amenities'>
                    <p>Whether you’re visiting South Lake Tahoe to hit the slopes or the beach, try your luck at the local
    casinos or just revel in the stunning year round scenery, the Stardust Lodge is your home away from
    home and just steps from the heart of Tahoe’s entertainment and shopping district.</p>
                    <p>-OUR HEATED POOL &amp; SPAS ARE OPEN AND HEATED YEAR ROUND. WE OPEN
    OUR SECOND POOL IN THE SUMMER.</p>
                    <p>Blending classic style and modern comfort in the heart of Lake Tahoe, we offer every amenity necessary
    for a relaxing stay, including complimentary touches like daily donuts, coffee and hot popcorn, Wi-Fi
    Internet, and DVD movie library.</p>
                    <div id='amenitiesList'>
                        <p>Other property features include:</p>
                        <ul>
                            <li>Fitness room</li>
                            <li>Free on-site laundry</li>
                            <li>Free public computer and printer</li>
                            <li>Free bikes &amp; seasonal equipment like sleds and snowshoes</li>
                            <li>BBQ grills and outdoor fire pits year round</li>
                            <li>Picnic tables (seasonal)</li>
                            <li>Activities Center, offering daily hosted group activities</li>
                        </ul>
                    </div>
                </div>
                {
                    rooms.map((room, index) => {
                        return (
                            index % 2 !== 0 
                            ? <>
                                <LeftRoom
                                    title={room.title}
                                    src={room.image}
                                    alt={room.alt}
                                    description={room.description}
                                />
                                {
                                    index !== rooms.length - 1
                                    ? <hr className='roomSeparator' style={{ margin: 'auto' }} />
                                    : ''
                                }
                            </>
                            : <>
                                <RightRoom
                                    title={room.title}
                                    src={room.image}
                                    alt={room.alt}
                                    description={room.description}
                                />
                                {
                                    index !== rooms.length - 1
                                    ? <hr className='roomSeparator' style={{ margin: 'auto' }} />
                                    : ''
                                }
                            </>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default RoomList;
