import axios from 'axios';

let rooms;
let room;
let roomTitles;

const RoomsService = {
    getRooms: async function() {
        const roomsUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/rooms`;
        await axios.get(roomsUrl).then((response) => {
            rooms = response.data;
        });

        return rooms;
    },
    getRoom: async function(roomId) {
        const roomUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/rooms/${roomId}`;
        await axios.get(roomUrl).then((response) => {
            room = response.data;
        });
        
        return room;
    },
    getRoomTitles: async function() {
        const roomsUrl = `${process.env.REACT_APP_STARDUST_BASE_URL}${process.env.REACT_APP_STARDUST_WP_API}/rooms?_fields=title`;
        await axios.get(roomsUrl).then((response) => {
            console.log(response);
            if (response.data) {
                response.data.forEach(room => {
                    roomTitles.push({ label: room.title.rendered, value: room.title.rendered });
                })
            }
        });

        return roomTitles;
    }
};

export default RoomsService;
