import React from 'react';
import { Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';

import navLogo from '../../assets/images/stardust_nav_logo.png';

import './navbar.css';

function StardustNavbar(props) {
    const handleLogout = () => {
        localStorage.removeItem('user-token');
        props.authChanger(false);
    }

    return (
        <Navbar expand='lg px-2'>
            <Navbar.Brand href='/stardust' className='nav-logo'>
                <img src={navLogo} alt='navigation logo' />
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="stardust-menu" />
            <Navbar.Collapse id="stardust-menu" className='justify-content-end'>
                <Nav>
                    <Nav.Link href="/stardust">Home</Nav.Link>
                    <Nav.Link href="/stardust/activities">Activities</Nav.Link>
                    {
                        props.isAuthenticated ? 
                            <Nav.Link href='/stardust/reservations'>Reservations</Nav.Link>
                        :
                        <></>
                    }
                    {
                        props.isAuthenticated ? 
                        <button id='logout' onClick={handleLogout}>Logout</button>
                        :
                        <Nav.Link href='/stardust/login'>Login</Nav.Link>
                    }
                </Nav>
            </Navbar.Collapse>
        </Navbar>
    )
}

export default StardustNavbar;
