                    //         <ImageParallax 
                    //             images={HTMLService.sanatizeHTML(room.excerpt.rendered).trim().split(',')}
                    //         />
                    //     </div>
                    // </>
                :
                    console.log('no')
                    // <>
                    //     <h1>No rooms found</h1>
                    // </>
            }
        </div>
    )
}

export default RoomPage;
