import React, { useState, useEffect } from "react";
import { Routes, Route } from 'react-router-dom';
import PrivateRoute from "./components/privateRoute/privateRoute";

import Navbar from './components/navbar/navbar';
import Footer from './components/footer/footer';
import HomePage from './pages/home/home';
import ActivitiesPage from './pages/activities/activities';
import ReservationPage from './pages/reservations/reservations';
import LoginPage from './pages/login/login';

import "./App.css";

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        if (localStorage.getItem('user-token')) {
            setIsAuthenticated(true);
        }
    }, []);

    return (
        <div className="App">
            <Navbar 
                isAuthenticated={isAuthenticated}
                authChanger={setIsAuthenticated}
            />
            <div classNmae='spacer'>&nbsp;</div>
                <Routes>
                    <Route path='/testsite' element={
                            <HomePage />
                        }
                    />
                    <Route path='/testsite/activities' element={
                            <ActivitiesPage />
                        }
                    />
                    <Route path='/testsite/reservations' element={
                            <PrivateRoute>
                                <ReservationPage />
                            </PrivateRoute>
                        }
                    />
                    <Route path='/testsite/login' element={
                            <LoginPage authChanger={setIsAuthenticated} />
                        }
                    />
                </Routes>
            <Footer />
        </div>
    );
}

export default App;
