import React, { useEffect, useState } from 'react';

import PageTitle from '../../components/pageTItle/pageTitle';

import Container from 'react-bootstrap/Container';

import HTMLService from '../../services/htmlService';
import NewslettersService from '../../services/newsletterService';

import './newsletters.css';

function NewslettersPage() {
    const [newsletter, setNewsletter] = useState({});
    const [newsletterList, setNewsletterList] = useState([]);
    

    useEffect(() => {
        async function getNewsletters() {
            let response = await NewslettersService.getNewsletters();

            if (response.length) {
                setNewsletter(response[0]);

                if (response.length > 1) {
                    response.shift();
                    setNewsletterList(response);
                }
            }
        }

        getNewsletters();
    }, [])

    return (
        <div id='newsletters'>
            <PageTitle
                image="#"
                pageTitle="Newsletters"
            />
            <Container>
                {
                    !newsletter.title ?
                        <h1>No newsletter found</h1>
                    :
                        <p>Here, you can find our most recent newsletter: <a target="_blank" rel="noopener noreferrer" href={HTMLService.sanatizeHTML(newsletter.content.rendered)}>{HTMLService.sanatizeHTML(newsletter.title.rendered)}</a></p>
                }
                {
                    !newsletterList.length ?
                        <h1>No Historical Newsletters Found</h1>
                    :
                        <>
                            <p>Here, you can find a list of historical newsletters:</p>
                            <ul>
                                {
                                    newsletterList.map((letter) => {
                                        return (
                                            <li>
                                                <a target="_blank" rel="noopener noreferrer" href={HTMLService.sanatizeHTML(letter.content.rendered)}>{HTMLService.sanatizeHTML(letter.title.rendered)}</a>
                                            </li>
                                        )
                                    })
                                }
                            </ul>
                        </>
                }
            </Container>
        </div>
    )
}

export default NewslettersPage;
