import React, { useRef, useState } from 'react';
import EmailService from '../../services/emailService';
import DatePicker from 'react-datepicker';

import './reservationRequest.css';
import "react-datepicker/dist/react-datepicker.css";

function ReservationRequest() {
    const [endDate, setEndDate] = useState(new Date());
    const [phone, setPhone] = useState('(XXX)XXX-XXXX');
    const [startDate, setStartDate] = useState(new Date());
    const [use, setUse] = useState('default');

    const nameRef = useRef('');
    const emailRef = useRef('');
    const accountRef = useRef('');

    const options = [
        { label: 'How do you plan to use this?', value: 'default' },
        { label: 'At Stardust 7-Night Week', value: 'At Stardust 7-Night Week' },
        { label: 'At Stardust Split Week', value: 'At Stardust Split Week' },
        { label: 'RCI Exchange', value: 'RCI Exchange' },
        { label: 'Interval International Exchange', value: 'Interval International Exchange' },
        { label: 'Rent Out', value: 'Rent Out' }
    ]

    const handleFormSubmit = (e) => {
        e.preventDefault();

        const isValid = validateForm();

        if (isValid) {
            const params = {
                'name': nameRef.current.value,
                'phone': phone,
                'email': emailRef.current.vale,
                'account': accountRef.current.value,
                'checkIn': startDate,
                'checkOut': endDate,
                'use': use
            };
            
            const success = EmailService.sendEmail(params);

            if (success) {
                resetForm();
            }
        }

    }

    const resetForm = () => {
        console.log('Form Reset');
    }

    const validateForm = () => {
        let validForm = true;

        return validForm;
    }

    const phoneChange = (e) => {
        setPhone(e.target.value);
    }

    const selectChange = (e) => {
        setUse(e.target.value);
    }

    return (
        <div id='reservation-request'>
            <h3>Request a Reservation</h3>
            <form onSubmit={handleFormSubmit}>
                <input id='name' type='text' name='name' placeholder='Name' ref={nameRef} required />
                <input id='email' type='email' name='email' placeholder='E-Mail' ref={emailRef} required />
                <input id='phone' type='text' name='phone' value={phone} onChange={phoneChange} required />
                <input id='account' type='number' name='account' placeholder='Account Number' ref={accountRef} />
                <DatePicker showIcon selected={startDate} onChange={(date) => setStartDate(date)} required />
                <DatePicker showIcon selected={endDate} onChange={(date) => setEndDate(date)} required />
                <select id='for' value={use} onChange={selectChange}>
                    {
                        options.map((option, index) => {
                            return <option value={option.value} key={index}>{option.label}</option>
                        })
                    }
                </select>
                <button type='submit'>Submit Request</button>
            </form>
        </div>
    )
}

export default ReservationRequest;
