import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

import './room.css';
import ImageParallax from '../../../components/imageParallax/imageParallax';
import HTMLService from '../../../services/htmlService';

function RoomPage() {
    const [room, setRoom] = useState([]);
    const { roomId } = useParams();

    useEffect(() => {
        fetch(`http://localhost/stardust/wp-json/wp/v2/rooms/${roomId}`)
            .then(response => response.json())
            .then(data => {
                setRoom(data);
                console.log(data);
            });
    }, []);

    useEffect(() => {
        console.log(room);
    }, [room])

    return (
        <div id='room'>
            {
                room ? 
                    <>
                        <h1>{ room.title }</h1>
                        <div className="image-container">
                            <ImageParallax 
                                images={HTMLService.sanatizeHTML(room.excerpt.rendered).trim().split(',')}
                            />
                        </div>
                    </>
                :
                    <>
                        <h1>No rooms found</h1>
                    </>
            }
        </div>
    )
}

export default RoomPage;
