import React, { useEffect, useState } from 'react';

import forestImage from '../../assets/images/forest.png';
import skiliftImage from '../../assets/images/skilift.jpg';
import lakeTahoeImage from '../../assets/images/lake_tahoe.png';
import fireworksImage from '../../assets/images/fireworks.jpg';
import skiingImage from '../../assets/images/skiing.png';
import lakeFunImage from '../../assets/images/lake_fun.png';

const homeImages = [
    {
        url: `url(${forestImage})`,
        alt: 'forest'
    },
    {
        url: `url(${skiliftImage})`,
        alt: 'skilift'
    },
    {
        url: `url(${lakeTahoeImage})`,
        alt: 'lake tahoe'
    },
    {
        url: `url(${fireworksImage})`,
        alt: 'fireworks'
    },
    {
        url: `url(${skiingImage})`,
        alt: 'skiing'
    },
    {
        url: `url(${lakeFunImage})`,
        alt: 'lake fun'
    }
]


function HomePage() {
    const [currentImage, setCurrentImage] = useState({});
    const [imageIndex, setImageIndex] = useState(0);
    
    useEffect(() => {
        setCurrentImage(homeImages[imageIndex]);

        setTimeout(() => {
            if (imageIndex > homeImages.length) {
                setImageIndex(0);
            } else {
                setImageIndex(imageIndex++);
            }
        }, 5000);
    }, [imageIndex])

    return (
        <div style={{ backgroundImage: currentImage.url }}>
            Hello World
        </div>
    )
}

export default HomePage;
