import React, { useEffect, useState } from 'react';
import HTMLService from '../../services/htmlService';

import './roomList.css';

import RoomCard from '../roomCard/roomCard';

function RoomList() {
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        fetch('http://localhost/stardust/wp-json/wp/v2/rooms')
            .then(response => response.json())
            .then(data => {
                setRooms(data);
            });
    }, []);

    return (
        <div id='room-list'>
            <div id='room-list-content'>
                <div className='rooms'>
                    {
                        rooms.map((room) => {
                            return (
                                <RoomCard
                                    key={room.id}
                                    id={room.id}
                                    title={HTMLService.sanatizeHTML(room.title.rendered)}
                                    images={HTMLService.sanatizeHTML(room.excerpt.rendered)}
                                    description={HTMLService.sanatizeHTML(room.content.rendered)}
                                />
                            )
                        })
                    }
                </div>
            </div>
        </div>
    )
}

export default RoomList;
