import React, { useEffect, useState } from 'react';
import { useLoaderData, useNavigate, useParams } from 'react-router-dom';

import ImageParallax from '../../../components/imageParallax/imageParallax';
import PageTitle from "../../../components/pageTItle/pageTitle";

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Carousel from "react-bootstrap/Carousel";

import HTMLService from '../../../services/htmlService';
import RoomsService from '../../../services/roomsService';

import './room.css';

function RoomPage() {
    const [images, setImages] = useState([]);
    const [room, setRoom] = useState({});
    const { roomId } = useParams();
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex) => {
        setIndex(selectedIndex);
    };

    const navigate = useNavigate();

    useEffect(() => {
        async function getRoom(roomId) {
            const selectedRoom = await RoomsService.getRoom(roomId);
            setRoom(selectedRoom);
            setImages(HTMLService.sanatizeHTML(selectedRoom.excerpt.rendered).split(','));
        };

        getRoom(roomId);
    }, []);

    const handleReservation = (e) => {
        e.preventDefault();
        navigate(`/stardust/reservations/${room.title.rendered}`);
    }

    return (
        <div id='room'>
            {
                room?.id  ? 
                    <>
                        <PageTitle 
                            image="#"
                            pageTitle={ room.title.rendered }
                        />
                        <Container>
                            <Carousel activeIndex={index} onSelect={handleSelect} interval={null}>
                                {
                                    images.map((image) => {
                                        return(
                                            <Carousel.Item>
                                                <div style={{ backgroundImage: `url(${image})`, height: '500px', width: '500px' }}></div>
                                            </Carousel.Item>
                                        )
                                    })
                                }
                            </Carousel>
                            <Row className='justify-content-center roomInfo'>
                                <Col xs={10} md={6}>
                                    <div className="image-container">
                                        <ImageParallax 
                                            images={HTMLService.sanatizeHTML(room.excerpt.rendered).split(',')}
                                        />
                                    </div>
                                </Col>
                                <Col xs={10} md={4}>
                                    <div className="description">
                                        <p>{ HTMLService.sanatizeHTML(room.content.rendered) }</p>
                                    </div>
                                </Col>
                            </Row>
                            <Row className="justify-content-center mt-3">
                                <Button onClick={handleReservation}>Reserve this Room</Button>
                            </Row>
                        </Container>
                    </>
                :
                    <>
                        <h1>No rooms found</h1>
                    </>
            }
        </div>
    )
}

export default RoomPage;
