import React, { useEffect, useRef } from 'react';
import HomeLanding from '../../components/homeLanding/homeLanding';
import RoomList from '../../components/roomList/roomList';

import axios from 'axios';

import './home.css';

function HomePage() {
    let roomRef = useRef(null);

    useEffect(() => {
        const sendEmail = async () => {
            const response = await axios.post('http://localhost/testsite/wp-json/wp/v2/reservation',
                {
                    to: 'ptj92e@gmail.com',
                    subject: 'Test EMAIL',
                    message: 'This is a test email from Stardust',
                    from: 'newEmail@gmail.com'
                }
            );
            console.log(response);
        }

        sendEmail();
    }, [])

    return (
        <div id='home'>
            <HomeLanding />
            <button onClick={() => roomRef.current?.scrollIntoView({ behavior: 'smooth' })}>Click Me</button>
            <RoomList ref={roomRef} />
        </div>
    )
}

export default HomePage;
