import React, { useEffect, useState } from 'react';

import forestImage from '../../assets/images/forest.png';
import skiliftImage from '../../assets/images/skilift.jpg';
import lakeTahoeImage from '../../assets/images/lake_tahoe.png';
import fireworksImage from '../../assets/images/fireworks.jpg';
import skiingImage from '../../assets/images/skiing.png';
import lakeFunImage from '../../assets/images/lake_fun.png';

const homeImages = [
    {
        url: `url(${forestImage})`,
        alt: 'forest'
    },
    {
        url: '../../assets/images/skilift.png',
        alt: 'skilift'
    },
    {
        url: '../../assets/images/lake_tahoe.png',
        alt: 'lake tahoe'
    },
    {
        url: '../../assets/images/fireworks.png',
        alt: 'fireworks'
    },
    {
        url: '../../assets/images/skiing.png',
        alt: 'skiing'
    },
    {
        url: '../../assets/images/lake_fun.png',
        alt: 'lake fun'
    }
]


function HomePage() {
    const [currentImage, setCurrentImage] = useState(homeImages[0]);
    const [imageIndex, setImageIndex] = useState(0);
    
    useEffect(() => {
        setCurrentImage(homeImages[imageIndex]);
    })

    return (
        <div style={{ backgroundImage: currentImage.url }}>
            Hello World
        </div>
    )
}

export default HomePage;
