import React, { useRef, useState } from 'react';
import DatePicker from 'react-datepicker';

import './reservationRequest.css';
import "react-datepicker/dist/react-datepicker.css";

function ReservationRequest() {
    const [startDate, setStartDate] = useState(new Date());
    const [endDate, setEndDate] = useState(new Date());
    const [use, setUse] = useState(options[0].value);

    const nameRef = useRef('');
    const emailRef = useRef('');
    const phoneRef = useRef('');
    const accountRef = useRef('');

    const options = [
        { label: 'How do you plan to use this?', value: 'default' },
        { label: 'At Stardust 7-Night Week', value: 'At Stardust 7-Night Week' },
        { label: 'At Stardust Split Week', value: 'At Stardust Split Week' },
        { label: 'RCI Exchange', value: 'RCI Exchange' },
        { label: 'Interval International Exchange', value: 'Interval International Exchange' },
        { label: 'Rent Out', value: 'Rent Out' }
    ]

    const handleFormSubmit = (e) => {
        e.preventDefault();

        const requestBody = {
            name: nameRef,
            phone: phoneRef,
            email: emailRef,
            account: accountRef,
            checkIn: startDate,
            checkOut: endDate,
            use: use
        }
        console.log(requestBody);
    }

    const handleChange = (e) => {
        setUse(e.target.value);
    }

    return (
        <div id='reservation-request'>
            <h3>Request a Reservation</h3>
            <form onSubmit={handleFormSubmit}>
                <input type='text' name='name' placeholder='Name' ref={nameRef} />
                <input type='email' name='email' placeholder='E-Mail' ref={emailRef} />
                <input type='number' name='phone' placeholder='Phone Number' ref={phoneRef} />
                <DatePicker showIcon selected={startDate} onChange={(date) => setStartDate(date)} />
                <DatePicker showIcon selected={endDate} onChange={(date) => setEndDate(date)} />
                <input type='number' name='account' placeholder='Account Number' ref={accountRef} />
                <select value={use} onChange={handleChange}>
                    {
                        options.map(option => {
                            return <option value={option.value}>{option.label}</option>
                        })
                    }
                </select>
                <button type='submit'>Submit Request</button>
            </form>
        </div>
    )
}

export default ReservationRequest;
