import React from 'react';
import { Link } from 'react-router-dom';

import navLogo from '../../assets/images/stardust_nav_logo.png';

import './navbar.css';

function Navbar(props) {
    const handleLogout = () => {
        localStorage.removeItem('user-token');
        props.authChanger(false);
    }

    return (
        <div id='navbar'>
            <Link id='nav-logo' to='/testsite'>
                <img src={navLogo} alt='navigation logo' />
            </Link>
            <ul id='nav-menu'>
                <li className='nav-item'>
                    <Link to='/testsite'>Home</Link>
                </li>
                <li className='nav-item'>
                    <Link to='/testsite/activities'>Activities</Link>
                </li>
                {
                    props.isAuthenticated ? 
                        <li className='nav-item'>
                            <Link to='/testsite/reservations'>Reservations</Link>
                        </li>
                    :
                    <></>
                }
                <li className='nav-item'>
                    {
                        props.isAuthenticated ?
                            <button id='logout' onClick={handleLogout}>Logout</button>
                        :
                        <Link to='/testsite/login'>Login</Link>
                    }
                </li>
            </ul>
        </div>
    )
}

export default Navbar;
