import React, { useEffect, useState } from 'react';

import PageTitle from '../../components/pageTItle/pageTitle';

import HTMLService from '../../services/htmlService';
import NewslettersService from '../../services/newsletterService';

import './newsletters.css';

function NewslettersPage() {
    const [newsletter, setNewsletter] = useState({});
    const [newsletterList, setNewsletterList] = useState([]);
    const [pdfUrl, setPdfUrl] = useState('');
    const [pdf, setPdf] = useState();

    useEffect(() => {
        async function getNewsletters() {
            let response = await NewslettersService.getNewsletters();

            if (response.length) {
                setNewsletter(response[0]);
                setPdfUrl(HTMLService.sanatizeHTML(response[0].content.rendered));

                if (response.length > 1) {
                    response.shift();
                    setNewsletterList(response);
                }
            }
        }

        getNewsletters();
    }, [])

    const openPdfInNewTab = (e) => {
      e.preventDefault();
      window.open(pdfUrl, '_blank');
    };

    return (
        <div id='newsletters'>
            <PageTitle
                image="#"
                pageTitle="Newsletters"
            />
            {
              !newsletter.title ?
                <h1>No newsletter found</h1>
              :
                <a target="_blank" onClick={openPdfInNewTab}>{HTMLService.sanatizeHTML(newsletter.title.rendered)}</a>
            }
        </div>
    )
}

export default NewslettersPage;
