import React, { useEffect, useState, useRef } from "react";
import { useParams } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import EmailService from "../../services/emailService";
import RoomsService from "../../services/roomsService";

import DatePicker from 'react-datepicker';

import './reservations.css';
import 'react-datepicker/dist/react-datepicker.css';

function ReservationPage() {
    const [endDate, setEndDate] = useState(new Date());
    const [phone, setPhone] = useState('');
    const [room, setRoom] = useState('default');
    const [roomTitles, setRoomTitles] = useState([{ label: 'Select an option...', value: 'default' }])
    const [startDate, setStartDate] = useState(new Date());
    const [use, setUse] = useState('default');

    const nameRef = useRef('');
    const emailRef = useRef('');
    const accountRef = useRef('');

    const { roomName } = useParams();

    const options = [
        { label: 'Select an option...', value: 'default' },
        { label: 'At Stardust 7-Night Week', value: 'At Stardust 7-Night Week' },
        { label: 'At Stardust Split Week', value: 'At Stardust Split Week' },
        { label: 'RCI Exchange', value: 'RCI Exchange' },
        { label: 'Interval International Exchange', value: 'Interval International Exchange' },
        { label: 'Rent Out', value: 'Rent Out' }
    ]

    useEffect(() => {
        async function fetchRoomTitles() {
            const roomTitleList = await RoomsService.getRoomTitles();
            const mergedArray = [...roomTitles, ...roomTitleList]
            setRoomTitles(mergedArray);
        }

        fetchRoomTitles();
        setRoom(roomName ? roomName : 'default');
    }, []);

    const handleFormSubmit = (e) => {
        e.preventDefault();

        const isValid = validateForm();

        if (isValid) {
            const params = {
                'name': nameRef.current.value,
                'phone': phone,
                'email': emailRef.current.vale,
                'account': accountRef.current.value,
                'checkIn': startDate,
                'checkOut': endDate,
                'use': use
            };
            
            const success = EmailService.sendEmail(params);

            if (success) {
                resetForm();
            }
        }

    }

    const resetForm = () => {
        console.log('Form Reset');
    }

    const validateForm = () => {
        let validForm = true;

        return validForm;
    }

    const formatPhoneNumber = (input) => {
        const cleanedInput = input.replace(/\D/g, ''); // Remove non-digit characters
        const formattedInput = `(${cleanedInput.slice(0, 3)}) ${cleanedInput.slice(3, 6)}-${cleanedInput.slice(6)}`;
        return formattedInput;
    };

    const phoneChange = (e) => {
        setPhone(formatPhoneNumber(e.target.value));
    }

    const useChange = (e) => {
        setUse(e.target.value);
    }

    const roomChange = (e) => {
        setRoom(e.target.value);
    }

    return (
        <div id='reservation-request'>
            <Form>
                <div className="row">
                    <Form.Group>
                        <Form.Label>Name</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="John Smith" 
                            ref={nameRef} 
                            required 
                        />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>E-Mail</Form.Label>
                        <Form.Control 
                            type="email" 
                            placeholder="John.Smith@example.com" 
                            ref={emailRef} 
                            required 
                        />
                    </Form.Group>
                </div>
                <div className="row">
                    <Form.Group>
                        <Form.Label>Phone Number</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="(XXX) XXX-XXXX" 
                            value={phone} 
                            onChange={phoneChange} 
                            required 
                        />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Account Number</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Account Number" 
                            ref={accountRef} 
                        />
                    </Form.Group>
                </div>
            </Form>
            <form onSubmit={handleFormSubmit}>
                <div className='row'>
                    <div className='input-group'>
                        <label htmlFor='name'>Name</label>
                        <input id='name' type='text' name='name' ref={nameRef} required />
                    </div>
                    <div className='input-group'>
                        <label htmlFor='email'>E-Mail</label>
                        <input id='email' type='email' name='email' placeholder='E-Mail' ref={emailRef} required />
                    </div>
                </div>
                <div className='row'>
                    <div className='input-group'>
                        <label htmlFor='phone'>Phone Number</label>
                        <input id='phone' type='text' name='phone' value={phone} onChange={phoneChange} placeholder="(XXX) XXX-XXXX" required />
                    </div>
                    <div className='input-group'>
                        <label htmlFor='account'>Account Number</label>
                        <input id='account' type='text' name='account' placeholder='Account Number' ref={accountRef} />
                    </div>
                </div>
                <div className='row'>
                    <DatePicker showIcon selected={startDate} onChange={(date) => setStartDate(date)} required />
                    <DatePicker showIcon selected={endDate} onChange={(date) => setEndDate(date)} required />
                </div>
                <div className='select-group'>
                    <label htmlFor='for'>How do you plan to use this?</label>
                    <select id='for' value={use} onChange={useChange}>
                        {
                            options.map((option, index) => {
                                return <option value={option.value} key={index}>{option.label}</option>
                            })
                        }
                    </select>
                </div>
                <div className='select-group'>
                    <label htmlFor='for'>Room Name</label>
                    <select id='for' value={room} onChange={roomChange}>
                        {
                            roomTitles.map((option, index) => {
                                return <option value={option.value} key={index}>{option.label}</option>
                            })
                        }
                    </select>
                </div>
                <button type='submit'>Submit Request</button>
            </form>
        </div>
    )
}

export default ReservationPage;
