import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

import ImageParallax from '../../../components/imageParallax/imageParallax';

import HTMLService from '../../../services/htmlService';
import RoomsService from '../../../services/roomsService';

import './room.css';

function RoomPage() {
    const [room, setRoom] = useState({});
    const { roomId } = useParams();

    useEffect(() => {
        async function getRoom(roomId) {
            const selectedRoom = await RoomsService.getRoom(roomId);
            setRoom(selectedRoom);
        };

        getRoom(roomId);
    }, []);

    return (
        <div id='room'>
            {
                room.id  ? 
                    <>
                        <h1>{ room.title.rendered }</h1>
                        <div className="image-container">
                            <ImageParallax 
                                images={HTMLService.sanatizeHTML(room.excerpt.rendered).split(',')}
                            />
                        </div>
                    </>
                :
                    <>
                        <h1>No rooms found</h1>
                    </>
            }
        </div>
    )
}

export default RoomPage;
