import React, { useEffect, useState } from 'react';
import { pdfjs, getPage } from 'pdfjs';
import PageFlip from 'react-pageflip';

import PageTitle from '../../components/pageTItle/pageTitle';

import HTMLService from '../../services/htmlService';
import NewslettersService from '../../services/newsletterService';

import './newsletters.css';

pdfjs.GlobalWorkerOptions.workerSrc = `https://unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.js`;

function NewslettersPage() {
    const [newsletter, setNewsletter] = useState({});
    const [newsletterList, setNewsletterList] = useState([]);
    const [pdfUrl, setPdfUrl] = useState('');

    useEffect(() => {
        async function getNewsletters() {
            let response = await NewslettersService.getNewsletters();

            if (response.length) {
                setNewsletter(response[0]);
                setPdfUrl(HTMLService.sanatizeHTML(response[0].content.rendered));

                if (response.length > 1) {
                    response.shift();
                    setNewsletterList(response);
                }
            }
        }

        getNewsletters();
    }, [])

    useEffect(() => {
        const fetchPDF = async () => {
            try {
                const pdfData = await fetch(pdfUrl);
                const pdfBlob = await pdfData.blob();
                const document = await pdfjs.getDocument(pdfBlob).promise;
                console.log(document);
                //setNumPages(numPages);
            } catch (error) {
            console.error('Error fetching PDF:', error);
            }
        };
  
        fetchPDF();
    }, [pdfUrl])

    const PDFViewer = ({ pdfUrl }) => {
        const [numPages, setNumPages] = useState(null);
        const [pages, setPages] = useState([]);
      
        useEffect(() => {
          const fetchPDF = async () => {
            try {
              const pdfData = await fetch(pdfUrl);
              const pdfBlob = await pdfData.blob();
              const pdfDocument = await pdfjs.getDocument({ data: pdfBlob }).promise;
      
              setNumPages(pdfDocument.numPages);
              const pagesArray = Array.from({ length: pdfDocument.numPages }, (_, index) => index + 1);
              setPages(pagesArray);
            } catch (error) {
              console.error('Error fetching PDF:', error);
            }
          };
      
          fetchPDF();
        }, [pdfUrl]);
      
        return (
          <div>
            {pages.map((pageNumber) => (
              <div key={pageNumber}>
                <Page pageNumber={pageNumber} pdfUrl={pdfUrl} />
              </div>
            ))}
          </div>
        );
      };
      
      const Page = ({ pageNumber, pdfUrl }) => {
        const [page, setPage] = useState(null);
      
        useEffect(() => {
          const fetchPage = async () => {
            const pdfData = await fetch(pdfUrl);
            const pdfBlob = await pdfData.blob();
            const pdfDocument = await pdfjs.getDocument({ data: pdfBlob }).promise;
            const pdfPage = await pdfDocument.getPage(pageNumber);
      
            setPage(pdfPage);
          };
      
          fetchPage();
        }, [pdfUrl, pageNumber]);
      
        if (!page) {
          return <div>Loading...</div>;
        }
      
        return (
          <div>
            {/* Render PDF page content here */}
            <img src={page} alt={`Page ${pageNumber}`} />
          </div>
        );
      };

    const FlipBook = ({ children }) => {
        return (
            <PageFlip>
                {children}
            </PageFlip>
        );
    };

    return (
        <div id='newsletters'>
            <PageTitle
                image="#"
                pageTitle="Newsletters"
            />
            <FlipBook>
                <PDFViewer />
            </FlipBook>
        </div>
    )
}

export default NewslettersPage;
